import java.util.Scanner; // To take user input
public class Lesson01Input {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    System.out.println("Hello");// simple print line statement
	 
	Scanner Davin = new Scanner(System.in); //Declaring a variable to store input
	 
	System.out.println(Davin.nextLine());//.nextLine() takes user input as string
	
	Davin.close();
	}                                        

}
